import { useState, useEffect } from 'react';
import axios from 'axios';

export default function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [games, setGames] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loginData, setLoginData] = useState({ email: '', password: '' });

  useEffect(() => {
    if (token) {
      axios
        .get('http://localhost:5000/api/favorites', {
          headers: { Authorization: token },
        })
        .then((res) => setFavorites(res.data));
    }
  }, [token]);

  useEffect(() => {
    axios.get('https://www.freetogame.com/api/games').then((res) => {
      setGames(res.data.slice(0, 10));
    });
  }, []);

  const login = async () => {
    const res = await axios.post('http://localhost:5000/api/auth/login', loginData);
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
    setUser(res.data.user);
  };

  const addFavorite = async (game) => {
    const res = await axios.post('http://localhost:5000/api/favorites', game, {
      headers: { Authorization: token },
    });
    setFavorites(res.data);
  };

  const removeFavorite = async (id) => {
    const res = await axios.delete(`http://localhost:5000/api/favorites/${id}`, {
      headers: { Authorization: token },
    });
    setFavorites(res.data);
  };

  return (
    <main className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">GameHub</h1>

      {!user ? (
        <div className="mb-6">
          <input
            type="email"
            placeholder="Email"
            className="border p-2 mr-2"
            value={loginData.email}
            onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Senha"
            className="border p-2 mr-2"
            value={loginData.password}
            onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
          />
          <button onClick={login} className="bg-blue-500 text-white px-4 py-2 rounded">
            Entrar
          </button>
        </div>
      ) : (
        <p className="mb-4">Bem-vindo, {user.username}!</p>
      )}

      <h2 className="text-xl font-semibold mb-2">Jogos Populares</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        {games.map((game) => (
          <div key={game.id} className="border rounded p-4 shadow">
            <h3 className="font-bold text-lg">{game.title}</h3>
            <p>{game.short_description}</p>
            {token && (
              <button
                onClick={() => addFavorite(game)}
                className="mt-2 bg-green-500 text-white px-3 py-1 rounded"
              >
                Favoritar
              </button>
            )}
          </div>
        ))}
      </div>

      {token && (
        <div>
          <h2 className="text-xl font-semibold mb-2">Seus Favoritos</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {favorites.map((game) => (
              <div key={game.id} className="border rounded p-4 shadow">
                <h3 className="font-bold text-lg">{game.title}</h3>
                <p>{game.short_description}</p>
                <button
                  onClick={() => removeFavorite(game.id)}
                  className="mt-2 bg-red-500 text-white px-3 py-1 rounded"
                >
                  Remover
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </main>
  );
}
